memberdef.initializer:
= "#version 110
" "uniform mat4 MVP;
" "attribute vec2 vPos;
" "varying vec2 texcoord;
" "void main()
" "{
" " gl_Position = MVP * vec4(vPos, 0.0, 1.0);
" " texcoord = vPos;
" "}
"

= "#version 110
" "uniform sampler2D texture;
" "uniform vec3 color;
" "varying vec2 texcoord;
" "void main()
" "{
" " gl_FragColor = vec4(color * texture2D(texture, texcoord).rgb, 1.0);
" "}
"
